mapper.select:
SELECT *
FROM serviceInfo
WHERE 1=1
AND srvcCLCode = #{serviceInfoDTO.srvcCLCode}
AND srvcCSCode = #{serviceInfoDTO.srvcCSCode}
AND progrmSj LIKE CONCAT('%', #{serviceInfoDTO.progrmSj}, '%')
AND mnnstNm = #{serviceInfoDTO.mnnstNm}
AND sidoCd = #{serviceInfoDTO.sidoCd}
AND progrmBgnde >= #{serviceInfoDTO.progrmBgnde}
AND progrmEndde <= #{serviceInfoDTO.progrmEndde}
ORDER BY noticeEndde,progrmBgnde LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE serviceinfo
SET progrmSttusSe = 3
WHERE noticeEndde < #{currentDate}
